    <script src="https://code.jquery.com/jquery-3.6.0.min.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/materialize/1.0.0/js/materialize.min.js"></script>
    
    <script>
        // Initialize Materialize components
        document.addEventListener('DOMContentLoaded', function() {
            var elems = document.querySelectorAll('select');
            M.FormSelect.init(elems);
        });

        function updateCampaignName() {
            const source = document.getElementById('sourceModifier').value;
            const medium = document.getElementById('utmMediumModifier').value;
            const country = document.getElementById('countryModifier').value;
            const language = document.getElementById('languageModifier').value;
            const theme = document.getElementById('campaignThemeModifier').value;
            
            let campaignName = 'oneroyal';
            if (source) campaignName += `-${source}`;
            if (medium) campaignName += `-${medium}`;
            if (country) campaignName += `-${country}`;
            if (language) campaignName += `-${language}`;
            if (theme) campaignName += `-${theme}`;
            
            document.getElementById('campaignName').value = campaignName;
        }

        function updateUTMMedium() {
            const medium = document.getElementById('utmMediumModifier').value;
            document.getElementById('utmMedium').value = medium;
        }

        function generateCampaignID() {
            const campaignId = 'CID-' + Date.now().toString(36).toUpperCase();
            const adsetId = 'AID-' + Math.random().toString(36).substr(2, 10).toUpperCase();
            document.getElementById('utmSource').value = `${campaignId},${adsetId}`;
        }

        function buildURL() {
            const website = document.getElementById('website').value;
            const campaignName = document.getElementById('campaignName').value;
            const utmSource = document.getElementById('utmSource').value;
            const utmMedium = document.getElementById('utmMedium').value;
            const utmTerm = document.getElementById('utmTerm').value;
            const utmContent = document.getElementById('utmContent').value;
            
            if (!website || !campaignName || !utmSource || !utmMedium) {
                M.toast({html: 'Please fill in all required fields!', classes: 'red'});
                return;
            }
            
            let url = new URL(website);
            url.searchParams.set('utm_campaign', campaignName);
            url.searchParams.set('utm_source', utmSource);
            url.searchParams.set('utm_medium', utmMedium);
            
            if (utmTerm) url.searchParams.set('utm_term', utmTerm);
            if (utmContent) url.searchParams.set('utm_content', utmContent);
            
            document.getElementById('utmResult').value = url.toString();
            document.getElementById('result').style.display = 'block';
            
            // Scroll to result
            document.getElementById('result').scrollIntoView({ behavior: 'smooth' });
        }

        function copyToClipboard() {
            const textarea = document.getElementById('utmResult');
            textarea.select();
            document.execCommand('copy');
            M.toast({html: 'URL copied to clipboard!', classes: 'green'});
        }
    </script>
</body>
</html>